          if (contact[i].phoneNumbers.length &gt; 1) {
            let s = contact[i].phoneNumbers.length;
            this.setState({flag:false})
    
            for (let size = 0; size &lt; s; size++) {
              let a = JSON.parse(JSON.stringify(contact));
