      let values = { &quot;a&quot;: &quot;A&quot;, &quot;B&quot;: &quot;x&quot;, &quot;c&quot;: &quot;C&quot; };
      let regex = new RegExp(&quot;\\b&quot; + Object.keys(values).join(&#39;|&#39;) + &quot;\\b&quot;, &#39;g&#39;)
      return inputStr.replace(regex, (m) =&gt; values[m] )
