        &quot;a&quot;: &quot;A&quot;,
        &quot;B&quot;: &quot;x&quot;,
        &quot;c&quot;: &quot;C&quot;
      };
      for (const [search, replace] of Object.entries(values)) {
        inputStr = inputStr.replace(search, replace);    
      }
      return inputStr;
    }

