--&gt;I removed the first **cout** of your code since you only need the one &quot;Input statement&quot; that is inside your while loop.

--&gt;I placed the conditional statements inside your while loop.

--&gt;I added a new else statement to place the word &quot;Invalid Operation&quot;
    

    using namespace std;
    
    int main()
    {
        float n1,n2,sum,diff,pro,quot; char f;
        
        while (f!=&#39;A&#39;,&#39;S&#39;,&#39;D&#39;,&#39;M&#39;,&#39;X&#39;){
            cout&lt;&lt;&quot;Please enter your function(A,S,D,M or X to end the program&quot;&lt;&lt;endl;
            cin&gt;&gt;f;
          
            if(f==&#39;X&#39;){
               return 0; 
            }
            else if (f==&#39;A&#39;){
                cout&lt;&lt;&quot;Add&quot;;
                sum=n1+n2; 
                cout&lt;&lt;sum&lt;&lt;endl;
            }
            else if (f==&#39;S&#39;){
                cout&lt;&lt;&quot;Subtract&quot;;
                if (n1&gt;n2){
                    diff=n1-n2;
                    cout&lt;&lt;diff&lt;&lt;endl;
                }
                else{
                    diff=n2-n1;
                    cout&lt;&lt;diff&lt;&lt;endl;
                }
            }
            else if (f==&#39;M&#39;){
                cout&lt;&lt;&quot;Multiply&quot;;
                pro=n1*n2; 
                cout&lt;&lt;pro&lt;&lt;endl;
            }
            else if (f==&#39;D&#39;){
                cout&lt;&lt;&quot;Divide&quot;;
                if (n1&gt;n2){
