        text-align: right;
        color: rgba(255,255,255,.4);
    }
    .menu-toggle{
        padding: 1em;
        position: absolute;
        top: .75em;
        right: .75em;
        cursor: pointer;
    }
    .hamburger,
    .hamburger::before,
    .hamburger::after{
        content: &#39;&#39;;
        display: block;
        background: #f8f4f4;
        height: 3px;
        width: 1.95em;
        border-radius: 3px;
        transition: all ease-in-out 500ms;
    }
    .hamburger::before{
        transform: translateY(-7px);
    }
    .hamburger::after{
        transform: translateY(4px);
    }
    .open .hamburger{
        transform: rotate(45deg);
    }
    .open .hamburger::before{
        opacity: 0;
    }
    .open .hamburger::after{
        transform: translateY(-3px) rotate(-90deg);
    }
    @media (min-width: 1077px){
        .menu-toggle{
            display: none;
        }
        .site-nav{
            height: auto;
