    $(&#39;.card&#39;).on(&#39;shown.bs.collapse&#39;, function(e) {
        var $card = $(this).closest(&#39;.card&#39;);
        setTimeout(function(){
        $(&#39;html,body&#39;).animate({
          scrollTop: $card.offset().top
        }, 500); //animation speed
      }, 500); //execution timeout
      });

