}

struct MyType&lt;X: U32Const&gt; {
    val: X::U32,
}

impl&lt;X: U32Const&lt;U32 = u32&gt;&gt; MyType&lt;X&gt; {
    fn do_something(&amp;self) -&gt; X::U32 {
        self.val * X::VAL
    }
}

