```
abstract class In[+A] {
  def future: Future[A]
  def receive(implicit d: Duration): A = {
    Await.result[A](future, d)
  }
  def ?[B](f: A =&gt; B)(implicit d: Duration): B = {
    f(receive)
  }
}
