    	vecs.push_back(make_pair(level, rightVector)); mergeSort(rightVector, level + 1);
    
    	merge(leftVector, rightVector, vectortoSort);
    	vecs.push_back(make_pair(MaxDepth +1 - level, vectortoSort));
    }
    
