        def __init__(self, name, heading):
            self.name = name
            self.heading = heading
    
    
    class Intro1(Main):
        def __init__(self, page_options):
            self.page_options = page_options
            super().__init__(name=&#39;Start&#39;, heading=&#39;Starting Screen&#39;)
    
    
    options = Intro1(page_options=&#39;\n1. Something\n2. Something\n3. Something&#39;)
    
    
    def print_page():
        global options
        print(&#39;&#39;&#39;
        #### {} ####
        
