The source is: [URLSession: Multipart Form-Data Requests | Swift 3, Xcode 8][1].
In my specific case I need to provide orderExtId as a parameter for the backend server to accept my image. Your case may vary, depending on the requirements of the backend. 

    func requestNativeImageUpload(image: UIImage, orderExtId: String) {
        
        guard let url = imageUploadEndpoint else { return }
        let boundary = generateBoundary()
        var request = URLRequest(url: url)
        
        let parameters = [&quot;order_ext_id&quot;: orderExtId]
    
        guard let mediaImage = Media(withImage: image, forKey: &quot;file&quot;) else { return }
    
        request.httpMethod = &quot;POST&quot;
    
        request.allHTTPHeaderFields = [
                    &quot;X-User-Agent&quot;: &quot;ios&quot;,
                    &quot;Accept-Language&quot;: &quot;en&quot;,
                    &quot;Accept&quot;: &quot;application/json&quot;,
                    &quot;Content-Type&quot;: &quot;multipart/form-data; boundary=\(boundary)&quot;,
                    &quot;ApiKey&quot;: KeychainService.getString(by: KeychainKey.apiKey) ?? &quot;&quot;
                ]
    
        let dataBody = createDataBody(withParameters: parameters, media: [mediaImage], boundary: boundary)
        request.httpBody = dataBody
    
        let session = URLSession.shared
        session.dataTask(with: request) { (data, response, error) in
            if let response = response {
                print(response)
            }
    
            if let data = data {
                do {
                    let json = try JSONSerialization.jsonObject(with: data, options: [])
                    print(json)
                } catch {
                    print(error)
                }
            }
            }.resume()
    }
    
    
    func generateBoundary() -&gt; String {
        return &quot;Boundary-\(NSUUID().uuidString)&quot;
    }
    
    func createDataBody(withParameters params: [String: String]?, media: [Media]?, boundary: String) -&gt; Data {
    
        let lineBreak = &quot;\r\n&quot;
        var body = Data()
    
        if let parameters = params {
            for (key, value) in parameters {
                body.append(&quot;--\(boundary + lineBreak)&quot;)
                body.append(&quot;Content-Disposition: form-data; name=\&quot;\(key)\&quot;\(lineBreak + lineBreak)&quot;)
                body.append(&quot;\(value + lineBreak)&quot;)
            }
        }
    
        if let media = media {
            for photo in media {
                body.append(&quot;--\(boundary + lineBreak)&quot;)
                body.append(&quot;Content-Disposition: form-data; name=\&quot;\(photo.key)\&quot;; filename=\&quot;\(photo.fileName)\&quot;\(lineBreak)&quot;)
                body.append(&quot;Content-Type: \(photo.mimeType + lineBreak + lineBreak)&quot;)
                body.append(photo.data)
                body.append(lineBreak)
            }
        }
    
        body.append(&quot;--\(boundary)--\(lineBreak)&quot;)
    
        return body
    }
    
    
    extension Data {
        mutating func append(_ string: String) {
            if let data = string.data(using: .utf8) {
                append(data)
            }
        }
    }
    
    
    struct Media {
        let key: String
        let fileName: String
        let data: Data
        let mimeType: String
        
        init?(withImage image: UIImage, forKey key: String) {
            self.key = key
            self.mimeType = &quot;image/jpg&quot;
            self.fileName = &quot;\(arc4random()).jpeg&quot;
            
            guard let data = image.jpegData(compressionQuality: 0.5) else { return nil }
            self.data = data
        }
    }


