    &lt;/div&gt;

               &lt;ul class=&quot;footer-navigation&quot;&gt;
                &lt;li&gt;&lt;a class=&quot;back&quot; id=&quot;backtoTop&quot; onclick=&quot;scrollToTop();return false;&quot;href=&quot;#&quot;&gt;&amp;uarr;Back to Top&lt;/a&gt;&lt;/li&gt;
                &lt;li&gt;&lt;a class=&quot;home&quot; href=&quot;#home&quot;&gt;Home&lt;/a&gt;&lt;/li&gt;
