     &lt;data attribute=&quot;name&quot;&gt;value2&lt;/data&gt;
     &lt;data attribute=&quot;stateen&quot;&gt;some-state2&lt;/data&gt;
     &lt;data attribute=&quot;country&quot;&gt;en-us&lt;/data&gt;

