                        // Invoke PositionSelected as DidFinishAnimating is only called when touch to swipe
                        Element.SendPositionSelected();
                        Element.PositionSelectedCommand?.Execute(null);
                    });
                }
            }

            prevPosition = Element.Position;
        }
    }

