        std::cout &lt;&lt; &quot;Dialog::handler\n&quot;;
    }
    void render() {
        auto button = new Button;
        // a lambda catching &quot;this&quot; Dialog.
        button-&gt;OnClick([this] { this-&gt;handler(); });
        delete button;                               // you didn&#39;t delete your button
    }
};

int main() {
