    [SqlUserDefinedExtractor(AtomicFileProcessing = false)]&lt;br&gt;
    public class CustomJSONExtractor : IExtractor&lt;br&gt;
    {&lt;br&gt;
        private readonly Encoding _encoding;&lt;br&gt;
        private readonly byte[] _row_delim;&lt;br&gt;
        private string DELIMITER = &quot;~&quot;;&lt;br&gt;
        public CustomJSONExtractor(Encoding encoding = null, string row_delim = &quot;\r\n&quot;)&lt;br&gt;
        {&lt;br&gt;
            _encoding = Encoding.UTF8;&lt;br&gt;
            _row_delim = _encoding.GetBytes(row_delim);&lt;br&gt;
        }&lt;br&gt;
        //Every json line in the raw file is transformed to a flat structure&lt;br&gt;
        public override IEnumerable&lt;IRow&gt; Extract(IUnstructuredReader input, IUpdatableRow output)&lt;br&gt;
        {&lt;br&gt;
            //Read the input line by line&lt;br&gt;
            foreach (Stream current in input.Split(_row_delim))&lt;br&gt;
            {&lt;br&gt;
                using (StreamReader lineReader = new StreamReader(current, this._encoding))&lt;br&gt;
                {&lt;br&gt;
                    //reads the entire line&lt;br&gt;&lt;b&gt;
                    string line = lineReader.ReadToEnd();&lt;br&gt;&lt;/b&gt;
                    //break the line to multiple variables&lt;br&gt;
                    output.Set(1, &quot;A~1&quot;);&lt;br&gt;
                    yield return output.AsReadOnly();&lt;br&gt;
                }&lt;br&gt;
            } &lt;br&gt;          
        }&lt;br&gt;
    }&lt;br&gt;
}&lt;br&gt;


