    func bulk_handler_long(b []string) error {
    	&lt;-time.After(time.Millisecond * 5)
    	for _, k := range b {
    		_ = len(k)
    	}
    	return nil
    }
    
    func stats(name string) func() {
    	fmt.Printf(&quot;======================\n&quot;)
    	fmt.Printf(&quot;%v\n&quot;, name)
    	start := time.Now()
    	return func() {
    		fmt.Printf(&quot;time to run %v\n&quot;, time.Since(start))
    		var ms runtime.MemStats
    		runtime.ReadMemStats(&amp;ms)
    		fmt.Printf(&quot;Alloc: %d MB, TotalAlloc: %d MB, Sys: %d MB\n&quot;,
    			ms.Alloc/1024/1024, ms.TotalAlloc/1024/1024, ms.Sys/1024/1024)
    		fmt.Printf(&quot;Mallocs: %d, Frees: %d\n&quot;,
    			ms.Mallocs, ms.Frees)
    		fmt.Printf(&quot;HeapAlloc: %d MB, HeapSys: %d MB, HeapIdle: %d MB\n&quot;,
    			ms.HeapAlloc/1024/1024, ms.HeapSys/1024/1024, ms.HeapIdle/1024/1024)
    		fmt.Printf(&quot;HeapObjects: %d\n&quot;, ms.HeapObjects)
    		fmt.Printf(&quot;\n&quot;)
    	}
    }
    
    func process(r io.Reader, h func(string) error) error {
    	errs := make(chan error)
    	workers := make(chan struct{}, 4)
    	var wg sync.WaitGroup
    	go func() {
    		scanner := bufio.NewScanner(r)
    		for scanner.Scan() {
    			workers &lt;- struct{}{} // acquire a token
    			wg.Add(1)
    			go func(line string) {
    				defer wg.Done()
    				if err := h(line); err != nil {
    					errs &lt;- err
    				}
    				&lt;-workers
    			}(scanner.Text())
    		}
    		wg.Wait()
    		if err := scanner.Err(); err != nil {
    			errs &lt;- err
    		}
    		close(errs)
    	}()
    	var err error
    	for e := range errs {
    		if e != nil &amp;&amp; err == nil {
    			err = e
    		}
    	}
    	return err
    }
    
    func processWorkers(r io.Reader, h func(string) error) error {
    	errs := make(chan error)
    	input := make(chan string)
    	y := 4
    	var wg sync.WaitGroup
    	for i := 0; i &lt; y; i++ {
    		wg.Add(1)
    		go func() {
    			defer wg.Done()
    			for line := range input {
    				if err := h(line); err != nil {
    					errs &lt;- err
    				}
    			}
    		}()
    	}
    	go func() {
    		scanner := bufio.NewScanner(r)
    		for scanner.Scan() {
    			input &lt;- scanner.Text()
    		}
    		close(input)
    		wg.Wait()
    		if err := scanner.Err(); err != nil {
    			errs &lt;- err
    		}
    		close(errs)
    	}()
    	var err error
    	for e := range errs {
    		if err == nil &amp;&amp; e != nil {
    			err = e
    		}
    	}
    	return err
    }
    
    func processBulk(r io.Reader, h func([]string) error) error {
    	errs := make(chan error)
    	input := make(chan []string)
    	y := 4
    	var wg sync.WaitGroup
    	for i := 0; i &lt; y; i++ {
    		wg.Add(1)
    		go func() {
    			defer wg.Done()
    			for bulk := range input {
    				if err := h(bulk); err != nil {
    					errs &lt;- err
    				}
    			}
    		}()
    	}
    	go func() {
    		scanner := bufio.NewScanner(r)
    		l := 50
			bulk := make([]string, l)
			i := 0
			for scanner.Scan() {
				text := scanner.Text()
				bulk[i] = text
				i++
				if i == l {
					copied := make([]string, l, l)
					copy(copied, bulk)
					i = 0
					input &lt;- copied
				}
			}
    		if len(bulk) &gt; 0 {
    			input &lt;- bulk
    		}
    		close(input)
    		if err := scanner.Err(); err != nil {
    			errs &lt;- err
    		}
    	}()
    	go func() {
    		wg.Wait()
    		close(errs)
    	}()
    	var err error
    	for e := range errs {
    		if err == nil &amp;&amp; e != nil {
    			err = e
    		}
    	}
    	return err
    }
    
    func processSeq(r io.Reader, h func(string) error) error {
    	scanner := bufio.NewScanner(r)
    	for scanner.Scan() {
    		text := scanner.Text()
    		if err := h(text); err != nil {
    			return err
    		}
    	}
    	return scanner.Err()
    }


