        private boolean anyInDeepSet(Set&lt;T&gt; set, Set&lt;Set&lt;T&gt;&gt; current) {
            boolean containing = false;
    
            for (Set&lt;T&gt; s : current) {
                for (T item : set) {
                    containing |= s.contains(item);
                }
            }
    
            return containing;
        }
