            children.forEach((child) =&gt; {
                const {name:cname} = child
                const fullName = `${name.toLowerCase()}/${cname.toLowerCase()}`
                if(!m[fullName]) m[fullName] =[]
                m[fullName].push(child)
            })
            return m
        },{})
