&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-css --&gt;

    #image-container {
      position: relative;
    }

    #image-container &gt; img,
    #image-container &gt; canvas {
      position: absolute;
      left: 0;
      top: 0;
    }

    div.scroll {
      height: 400px;
      overflow: auto;
      white-space: nowrap;
    }

&lt;!-- language: lang-html --&gt;

    &lt;div id=&quot;image-container&quot; class=&quot;scroll&quot;&gt;
        &lt;canvas&gt;&lt;/canvas&gt;
