&lt;!-- language: lang-html --&gt;

    &lt;h2&gt;My First JavaScript&lt;/h2&gt;
    &lt;input type=&quot;text&quot; id=&quot;txt1&quot;&gt;
    &lt;p id=&quot;msg1&quot;&gt;&lt;/p&gt;
    &lt;input type=&quot;text&quot; id=&quot;txt2&quot; onkeyup=&quot;myFun()&quot;&gt;
