    &lt;section class=&quot;app-card app-amount-summary app-categories__card&quot;&gt;
      &lt;%= render &#39;category_depth_0&#39;, category: cat_depth_0 %&gt;

      &lt;% if cat_depth_0[:sub_categories] %&gt;
