    DECLARE     @foodGUID_1 NVARCHAR(360) = N&#39;ABABABA1-FGH-1234-ZX12-123456EDVERT&#39;,
                @foodGUID_2 NVARCHAR(360) = N&#39;GHDF1234-CVB-4312-CV15-678912NKOMLP&#39;,
