    &quot;METER&quot;,&quot;&quot;,&quot;U&quot;,&quot;S1&quot;,6.0000,&quot;&quot;,&quot;&quot;,20171108,&quot;S&quot;,,
    &quot;REGST&quot;,&quot;&quot;,&quot;METER&quot;,5,&quot;SCMH&quot;,1.000
    &quot;READG&quot;,20180216,,&quot;00990&quot;
