      &lt;ul class=&quot;secondary-filter__list&quot;&gt;
        &lt;li class=&quot;secondary-filter__list-item&quot;&gt;
          &lt;input type=&quot;radio&quot; id=&quot;secondaryFilter-all&quot; name=&quot;secondaryFilter&quot; value=&quot;All&quot;&gt;
          &lt;label for=&quot;secondaryFilter-all&quot;&gt;All&lt;/label&gt;
        &lt;/li&gt;

        &lt;li class=&quot;secondary-filter__list-item&quot;&gt;
