        ~MyStream()
        {
            if (pptr() - pbase() &lt; 2)
            { sync(); }
        }
    
    protected:
        virtual int sync() override
        {
            // make sure there actually is something to sync
            if (pptr() - pbase() &lt; 2)
            { return 0; }
    
            const std::string message{str()};
            const std::size_t length{message.length()};
    
            // update the pointers for the next sync
            setp(pbase() + length, epptr());
    
