      v_data varchar2(50);
      v_data_VERSION varchar2(50);
      v_data_RECUR varchar2(50);
      v_data_RECUR_VERSION varchar2(50);
    
      cursor cur_data is 
        select parent_data,child_data,child_version
          from sample_table
          where parent_data = in_parent AND
                parent_version = IN_VERSION;
