      DBMS_OUTPUT.PUT_LINE(&#39;PROC_REPEAT_I3 : START - IN_PARENT=&#39; || IN_PARENT ||
                                        &#39; IN_VERSION=&#39; || IN_VERSION ||
                                        &#39; OUT_CHILD=&#39; || OUT_CHILD);
    
      open cur_data;
      loop
        fetch cur_data
