So change your null check to a length count, or use SingleOrDefault.

    var users = UserContext
        .User
        .AsQueryable()
        .Where
        (
            i =&gt; i.id == id
        )
        .ToList();
    if (users.Count != 1)
    {
        return NotFound();
    }
    else
    {
        return users.Single();
    }

Or

    var user = UserContext
        .User
        .AsQueryable()
        .SingleOrDefault
        (
            i =&gt; i.id == id
        );
    if (user == null)
    {
        return NotFound();
    }
    else
    {
        return user;
    }

