impl&lt;&#39;a&gt; Iterator for IpldIter&lt;&#39;a&gt; {
    type Item = &amp;&#39;a Ipld;

    fn next(&amp;mut self) -&gt; Option&lt;Self::Item&gt; {
        loop {
            if let Some(iter) = self.stack.last() {
                if let Some(ipld) = iter.next() {
                    match ipld {
                        Ipld::List(list) =&gt; {
                            self.stack.push(Box::new(list.iter()));
                        }
                        _ =&gt; {}
                    }
                    return Some(ipld);
                } else {
                    self.stack.pop();
                }
            } else {
                return None;
            }
        }
    }
}
