                WR_OF, WR, CMD_DIS_CFG_I, WR, Z00, WR, Z00, WR_CF,
                //Bypass (NOP)
                WR_OF, WR, CMD_BYPASS,    WR, FF,  WR, FF,  WR,  FF, WR_CF,
    
