                     wb_wr_data &lt;= (cur_cmd == WR_OF) ? 8&#39;h80 :
                               (cur_cmd == WR_CF ? 8&#39;h00 : (is_cmd_wre ? ufm_wr_data : next_cmd));
                     addr &lt;= (cur_cmd == WR_OF || cur_cmd == WR_CF) ? 8&#39;h70 : 8&#39;h71;
