        if(!wr_in_progress &amp;&amp; !rd_in_progress)
        begin
             if(wr_rq)
             begin
                wr_in_progress &lt;= 1;            
             end
             else if(rd_rq)
             begin
                rd_in_progress &lt;= 1;
             end
        end
        else if(wr_in_progress &amp;&amp; wb_ack)
        begin
             wr_in_progress &lt;= 0;
        end
        else if(rd_in_progress &amp;&amp; wb_ack)
        begin
