    def min_sum(g):
        if not g or not g[0]:
            return float(&#39;inf&#39;)
        return g[0][0] + ((len(g) &gt; 1 or len(g[0]) &gt; 1) and min(map(min_sum, (g[1:], [*zip(*g)][1:]))))

