                        global $wp_rewrite;
    
                        if ( $wp_rewrite &amp;&amp; $wp_rewrite instanceof \WP_Rewrite )
                            $v[&#39;base&#39;] = str_replace( 99999, &#39;%#%&#39;, esc_url( get_pagenum_link(99999) ) );
                    }
                    break;
            }
    
            return $v;
