    def place_by_getitem(string):
        pattern = &#39;(?&lt;!\w)(f)\[([^\[]+?)\]|(\[[^\[]+?\])&#39;
        while re.search(pattern, string):
            string = re.sub(pattern, get_repl, string)
    
        return string.replace(&#39;##1##&#39;, &#39;[&#39;).replace(&#39;##2##&#39;, &#39;]&#39;)
    
    
