    int len = 0;

    for (int i = 0; i &lt; a; i++) {
        int r = rand() % 30 + 1;

        printf(&quot;%d\n&quot;, r);
        len += sprintf(array[i], &quot;%d&quot;, r);
    }

    char *array_2 = malloc(len + 1);

    *array_2 = &#39;\0&#39;;
    for (int i = 0; i &lt; a; i++) {
        strcat(array_2, array[i]);
    }
    
    printf(&quot;\&quot;%s\&quot;\n&quot;, array_2);
    free(array_2);

