&lt;!-- language: lang-js --&gt;

    class Example {
      constructor(name, age) {
        this.name = name;
        this.age = age;
        this.method2();
      }
      method1(evt) {
        console.log(evt)
        alert(&quot;Example Rendered&quot;)
      }

      method2() {
        document.getElementById(&quot;button&quot;).onclick = this.method1;
      }

    }

