&lt;!-- language: lang-css --&gt;

    .\30 {
      color: red;
    }
    .\31 {
      color: blue;
    }
    .\31  .\30 {
      color: orange;
    }
    .\31 .\30 {
      color: green;
    }

&lt;!-- language: lang-html --&gt;

    &lt;div class=&quot;0&quot;&gt;.0&lt;/div&gt;
    &lt;div class=&quot;1&quot;&gt;
      .1
