    &quot;filter&quot; : {
    		&quot;expression&quot; : &quot;#path.#filter = :${fp}&quot;,  ## filter path parent.target = :target
            &quot;expressionNames&quot;: {
            	&quot;#path&quot; : &quot;${path}&quot;,
            	&quot;#filter&quot; : &quot;${fp}&quot;
            },
            &quot;expressionValues&quot; : {
            	&quot;:${fp}&quot; : $util.dynamodb.toDynamoDBJson(${$target[$fp].eq}) ## :target : value to filter for
            }
    }


  [1]: https://stackoverflow.com/questions/57963008/how-to-filter-by-query-on-nested-fields-in-aws-appsync
  [2]: https://docs.aws.amazon.com/amazondynamodb/latest/developerguide/Expressions.ExpressionAttributeNames.html#Expressions.ExpressionAttributeNames.AttributeNamesContainingDots

