    function X () {
      return new Promise((resolve, reject) =&gt; {
        if (x) {
          ///...
          resolve();
        } else {
          ///...
          reject();
        }
      })
      .then(() =&gt; axios.post(url, data))
      .then(response =&gt; {
        //...
        console.log(&quot;post complete&quot;);
      })
      .then(() =&gt; {
        ///...
