int algorithmOne(int n){
    int sum = 0;
    for(int i = 0; i &lt; n/2; i++){
        for(int j = 1; j &lt;= n/4; j= j *2){
            sum++;
        }
    }
    return sum;
}

int algorithmTwo(int n){
    int sum = 0;
    for(int i = 0; i &lt; n/2; i++){
