&lt;!-- language: lang-js --&gt;

    class BankAccount {
        constructor (amount = 0) {
            this.total = amount;
        }
        balance(amount) {
            return this.amount
        }

        withdraw (amount) {
            this.amount = this.amount - amount;
            return this.amount
        }

        deposit(amount) {
            this.amount = this.amount + amount;
            return this.amount
        }
    }

