    &lt;mat-radio-group
      aria-labelledby=&quot;example-radio-group-label&quot;
      class=&quot;example-radio-group&quot;
      [(ngModel)]=&quot;boxChecked&quot;&gt;
      &lt;mat-radio-button class=&quot;example-radio-button&quot; [value]=&quot;boxChecked&quot;&gt;
