      b1s.defineData(&#39;b1&#39;);
      b1s.defineDone();
    
      declare hash c1s();
      c1s.defineKey(&#39;d&#39;, &#39;e&#39;);
      c1s.defineData(&#39;c1&#39;);
      c1s.defineDone();
    
      declare hash d1s();
      d1s.defineKey(&#39;a&#39;, &#39;c&#39;);
      d1s.defineData(&#39;d1&#39;);
      d1s.defineDone();
    
      do while (not end);
        set have end=end;
        if a1s.find() = 0 then a1+a; else a1=a;  a1s.replace();
        if b1s.find() = 0 then b1+b; else b1=b;  b1s.replace();
        if c1s.find() = 0 then c1+c; else c1=c;  c1s.replace();
        if d1s.find() = 0 then d1+d; else d1=d;  d1s.replace();
      end;
    
      do while (not last);
        set have end=last;
        a1s.find();
        b1s.find();
