                    &gt;
                        &lt;TextInput
                            placeholder={&#39;Pseudo&#39;}
                            onChangeText={(text) =&gt; this.setState({pseudo: text})}
                        /&gt;
                        &lt;Button
                            styleName=&quot;secondary&quot;
                            style={{ marginTop: 20}}
                            onPress={this.onSubmit}
                        &gt;
                            &lt;Text&gt;ACCEDER&lt;/Text&gt;
                        &lt;/Button&gt;
                    &lt;/ImageBackground&gt;
                    {/*Modal section*/}
