                                    &lt;Image
                                        styleName=&quot;medium-avatar&quot;
                                        source={moi}
                                    /&gt;
                                    &lt;View styleName=&quot;content&quot;&gt;
                                        &lt;ScrollView&gt;
                                            &lt;Subtitle&gt;
                                               lorum ipsum
                                            &lt;/Subtitle&gt;
                                            {/*&lt;Subtitle style={{ color: &#39;red&#39;}}&gt;
                                                lorum ipsum
                                            &lt;/Subtitle&gt;*/}
                                            &lt;Caption&gt;Cr&#233;&#233; par Mama DEMBELE aka Pakendux&lt;/Caption&gt;
                                        &lt;/ScrollView&gt;
