                                &lt;/Card&gt;
                            &lt;/Modal&gt;
                        &lt;/Portal&gt;
                    &lt;/Provider&gt;
                &lt;/KeyboardAvoidingView&gt;
            );
        }
    }
    export default withSnackbar(Welcome)

