      if let notification = CKNotification(fromRemoteNotificationDictionary: dict), let subscriptionID = notification.subscriptionID{
        print(&quot;iOS Notification: \(subscriptionID)&quot;)
        switch subscriptionID{
          case &quot;a&quot;:
