def NormalizeMatrix(mat) :
    
    mat = np.array(mat);
    minMat = np.min(mat, axis=0, keepdims=1);
    maxMat = np.max(mat, axis=0, keepdims=1);
    
    sink = (mat - minMat)/ (maxMat - minMat);

    return sink;
